<template>
  <div id="app">
    <div class="attendee" v-if="scanner_status == 'off'">
      <h3 class="name">Attender: {{ first_name }} {{ last_name }}</h3>
      <h3 class="t-shirt">T-Shirt: {{ t_shirt_size }}</h3>
      <h1 class="green" v-if="status != '1'">TICKET IS OK</h1>
      <h1 class="red" v-if="status == '1'">ALREADY CHECKED</h1>
    </div>

    <button v-on:click="scannerStatusChange" v-if="scanner_status == 'off'">SCAN CODE</button>
    <button v-on:click="pushStatusUpdate" v-if="push_button_status == '1'">PUSH UPDATE</button>
    <div id="status-update" v-if="push_button_status == '0'">{{ push_status }}</div>
    <qrcode-stream v-if="scanner_status == 'on'" @decode="onDecode" @init="onInit" />

    <p class="decode-result">Last result: <b>{{ result }}</b></p>
    <p class="error">Error: {{ error }}</p>
  </div>
</template>

<script>

    import axios from 'axios';

    export default {
        data () {
            return {
                result: '',
                error: '',
                first_name: '',
                last_name: '',
                t_shirt_size: '',
                status: '',
                scanner_status: 'off',
                push_button_status: 0,
                attender_id: '',
                push_status: '',
            }
        },

        methods: {
            pushStatusUpdate () {
                // @TODO add security
                fetch('/admin/dc/attendee-data-update/' + this.attender_id, {
                    credentials: "include",
                    method: "GET",
                }).then(response => {
                    return response.json()
                }).then(json => {
                    this.push_status = json.toString();
                    this.push_button_status = 0;
                })
            },
            scannerStatusChange() {
                if (this.scanner_status === 'on') {
                    this.scanner_status = 'off';
                    this.push_button_status = 1;
                } else {
                    this.scanner_status = 'on';
                    this.push_button_status = 0;
                }
            },
            onDecode(result) {
                let items = result.split(':');
                this.result = result;
                axios.get('/admin/dc/attendee-data/' + items[0] + '/' + items[1] + '/' + items[2])
                    .then(response => {
                        // JSON responses are automatically parsed.
                        this.first_name = response.data.first_name;
                        this.last_name = response.data.last_name;
                        this.status = response.data.status;
                        this.t_shirt_size = response.data.t_shirt_size;
                        this.attender_id = response.data.attender_id;
                        if (this.t_shirt_size != '') {
                            this.beep();
                            this.scannerStatusChange();
                            this.push_button_status = 1;
                        }
                    })
            },

            async onInit(promise) {

                try {
                    await promise
                } catch (error) {

                    if (error.name === 'NotAllowedError') {
                        this.error = "ERROR: you need to grant camera access permisson"
                    } else if (error.name === 'NotFoundError') {
                        this.error = "ERROR: no camera on this device"
                    } else if (error.name === 'NotSupportedError') {
                        this.error = "ERROR: secure context required (HTTPS, localhost)"
                    } else if (error.name === 'NotReadableError') {
                        this.error = "ERROR: is the camera already in use?"
                    } else if (error.name === 'OverconstrainedError') {
                        this.error = "ERROR: installed cameras are not suitable"
                    } else if (error.name === 'StreamApiNotSupportedError') {
                        this.error = "ERROR: Stream API is not supported in this browser"
                    }
                }
            },

            beep() {
                let snd = new Audio("data:audio/wav;base64,");
                snd.play();
            }
        }
    }
</script>

<style>
#app {
  font-family: 'Avenir', Helvetica, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-align: center;
  color: #2c3e50;
  margin-top: 60px;
}
button {
  background: #5eb95e;
  color: #4c4c4c;
  padding: 15px;
  font-size: 2em;
  border-radius: 5px;
  margin: 20px;
}
#status-update {
  padding: 15px;
  border: 1px solid black;
}
.red {  color: red;}
.green { color: green; }

</style>
